INSERT IGNORE INTO `libros` (`id`, `titulo`, `subtitulo`, `autores`, `editorial`, `fecha_publicacion`, `descripcion`, `isbn_10`, `isbn_13`, `paginas`, `categoria`, `imagen_url`, `idioma`, `preview_link`, `fecha_importacion`) VALUES ('2oZACQAAQBAJ', 'América Latina en 130 documentales', NULL, 'Jorge Ruffinelli', 'UQBAR', '2012', 'El cine documental se convirtió, desde los años 50 en adelante, en el instrumento favorito para configurar una "historia secreta" de América Latina, desde sus sucesos mínimos hasta los grandes procesos de cambio colectivo. Todas las dimensiones del continente han sido exploradas y liberadas de su silencio por este modo fílmico. El estatuto autónomo y distintivo del documental ha encontrado en América Latina su afirmación más tenaz. Jorge Ruffinelli, experto y coleccionista como no existe otro, recorre aquí esa historia a través de muchos países, en una investigación sin precedentes, con imágenes evocadoras que explican cómo ha llegado a ser tan poderoso. No hay libro igual en español ni en ningún otro idioma: fundamental y único.', '956917109X', '9789569171093', 280, 'Performing Arts', 'http://books.google.com/books/content?id=2oZACQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'es', 'http://books.google.es/books?id=2oZACQAAQBAJ&printsec=frontcover&dq=Documentales&hl=&cd=2&source=gbs_api', '2026-01-20 18:22:54');
INSERT IGNORE INTO `libros` (`id`, `titulo`, `subtitulo`, `autores`, `editorial`, `fecha_publicacion`, `descripcion`, `isbn_10`, `isbn_13`, `paginas`, `categoria`, `imagen_url`, `idioma`, `preview_link`, `fecha_importacion`) VALUES ('3-twDwAAQBAJ', 'El documental político en Argentina, Chile y Uruguay', 'De los años cincuenta a la década del dos mil', 'Antonio Traverso, Tomás Crowder-Taraborrelli', 'LOM Ediciones', '2015', 'El cine documental en Argentina, Chile y Uruguay ha demostrado fuerza, sofisticación y deseos de ser tanto testigo de los eventos políticos como protagonista de los procesos sociales que han marcado a estos tres países desde la década de los cincuenta. El cine documental del Cono Sur constituye hoy día un corpus de trabajo y un archivo audiovisual histórico sustancial, que posee un gran potencial como fuente para investigar y teorizar tanto las historias y experiencias de cambio social y cultural en América Latina como el mismo género documental.', '9560006207', '9789560006202', 227, 'Art', 'http://books.google.com/books/content?id=3-twDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'es', 'http://books.google.es/books?id=3-twDwAAQBAJ&pg=PT200&dq=Documentales&hl=&cd=8&source=gbs_api', '2026-01-20 18:22:54');
