INSERT IGNORE INTO `libros` (`id`, `titulo`, `subtitulo`, `autores`, `editorial`, `fecha_publicacion`, `descripcion`, `isbn_10`, `isbn_13`, `paginas`, `categoria`, `imagen_url`, `idioma`, `preview_link`, `fecha_importacion`) VALUES ('bPBfzH3dEzIC', 'Tipología documental de universidades', 'propuestas de identificación y valoración', 'Grupo de Trabajo de Identificación y Valoración de Series Administrativas de Archivos de Universidades, Carlos Asensio Die, Pilar Gil García', 'Univ de Castilla La Mancha', '2002', 'Identificar, valorar, eliminar, conservar...verbos conjugados, por activa y por pasiva, por archiveros en ejercicio de las últimas décadas. La identificación y valoración de fondos documentales han sido -son- el tema estrella de la archivística más reciente y, al mismo tiempo, la necesidad más acuciante para el profesional que asume la responsabilidad de la eliminación para la conservación. Sin estudios previos de identificación, la eliminación se convierte en una suerte de salto al vacío sin red con escasas probabilidades de éxito. La obra Tipología Documental de Universidades: Propuestas de Identificación y Valoración no es sino la materialización de los resultados de un proyecto cooperativo desarrollado por los archiveros de once universidades españolas -públicas y privadas- en el seno del Grupo de Identificación y Valoración de Series Administrativas de Archivos de Universidades, y buena prueba de que el ámbito de los archivos universitarios se está perfilando, desde hace unos años, como uno de los más prometedores dentro del panorama archivístico español.', '8484272133', '9788484272137', 228, 'History', 'http://books.google.com/books/content?id=bPBfzH3dEzIC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'es', 'http://books.google.es/books?id=bPBfzH3dEzIC&pg=PA16&dq=Documentales&hl=&cd=1&source=gbs_api', '2026-01-20 18:22:54');
